import React, { Component } from 'react'
import {View, Button, TextInput,FlatList,Text, TouchableOpacity} from 'react-native'

import firebase from 'firebase'
require("firebase/firestore")

export class Home extends Component {

    constructor(props){
        super(props);
        this.state={
            username:'',
            userList: []
        }
        this.searchUser = this.searchUser.bind(this)
    }

    componentDidMount(){
        // get username
        firebase.firestore()
            .collection('users')
            .doc(firebase.auth().currentUser.uid)
            .get()
            .then((infos)=>{
                this.setState({username:infos.data().name})
            })
            // get user id
            .then(()=>{
                firebase.firestore()
                .collection('followers')
                .where('abonne','==',this.state.username)
                .get()
                .then((infos)=>{
                    infos.docs.map(doc => {
                        console.log(doc)
                    })
                })
            })
    }

    searchUser(search){
        let myUsers = []
        firebase.firestore()
            .collection('users')
            .where('name','>=',search)
            .get()
            .then((infos)=>{
                infos.docs.map(doc => {
                    myUsers.push(doc)
                })
                this.setState({userList: myUsers})
            })
    }


    render() {
        return (
            <View>
                <Text>Welcome Home ! </Text>                
                <Button title="Add Picture" onPress={()=> {this.props.navigation.navigate('addPicture')}}/>
                <Button title="Go to home page" onPress={()=> {this.props.navigation.navigate('homePage',{res:this.state.username})}}/>
                <View>
                    <TextInput placeholder="Search User" onChangeText={(search)=>this.searchUser(search)}/>
                    <FlatList
                        numColumns={1}
                        horizental={false}
                        data={this.state.userList}
                        renderItem={({item})=>(
                            <TouchableOpacity onPress={()=>this.props.navigation.navigate('friendPage',{res: item})}>
                                <Text>{item.data().name}</Text>
                            </TouchableOpacity>
                        )} />
                </View>
                <Button title="Log Out" onPress={()=> {this.props.navigation.navigate('Logout')}} />

            </View>
            )
    }
}

export default Home
