import React, { Component } from 'react'
import {View, Button, TextInput,FlatList,Text, TouchableOpacity} from 'react-native'

import firebase from 'firebase'
require("firebase/firestore")

export class Home extends Component {

    constructor(props){
        super(props);
        this.state={
            username:'',
            userList: [],
            userFriends: [],
            allUsers:[],
            friendsInfo:[]
        }
        this.searchUser = this.searchUser.bind(this)
    }

    componentDidMount(){
        let myFriends = []
        let timelinePosts = []        
        let users = []
        let myFriendsInfo = []
        // get username
        console.log("get username")
        firebase.firestore()
            .collection('users')
            .doc(firebase.auth().currentUser.uid)
            .get()
            .then((infos)=>{
                this.setState({username:infos.data().name})
            })
            // get user friends
            .then(()=>{
                console.log("get user friends")
                firebase.firestore()
                .collection('followers')
                .where('abonne','==',this.state.username)
                .get()
                .then((infos)=>{
                    infos.docs.map(doc => {
                        myFriends.push(doc.data().abonnement)                        
                    })
                    this.setState({userFriends:myFriends})
                })
            //})
            // get the userList
            .then(()=>{
                console.log("get the userList")
                firebase.firestore()
                .collection('users')
                .get()
                .then((infos)=>{
                    infos.docs.map(doc => {
                        users.push(doc)
                    })
                    this.setState({allUsers: users})
            //})

            // get the full information of my friends
            .then(()=>{
                console.log(this.state.userFriends)
                console.log(this.state.userFriends.length)
                for (var i in this.state.userFriends){
                    for (var j in this.state.allUsers){
                        console.log(this.state.userFriends[i] +" == "+this.state.allUsers[j].data().name)
                        if (this.state.userFriends[i] == this.state.allUsers[j].data().name){
                            myFriendsInfo.push(this.state.allUsers[j])
                            break;
                        }
                    }
                } 
            })
        })
        })
        })
    }

    searchUser(search){
        let myUsers = []
        firebase.firestore()
            .collection('users')
            .where('name','>=',search)
            .get()
            .then((infos)=>{
                infos.docs.map(doc => {
                    myUsers.push(doc)
                })
                this.setState({userList: myUsers})
            })
    }


    render() {
        return (
            <View>
                <Text>Welcome Home ! </Text>                
                <Button title="Add Picture" onPress={()=> {this.props.navigation.navigate('addPicture')}}/>
                <Button title="Go to home page" onPress={()=> {this.props.navigation.navigate('homePage',{res:this.state.username})}}/>
                <View>
                    <TextInput placeholder="Search User" onChangeText={(search)=>{this.searchUser(search)}}/>
                    <FlatList
                        numColumns={1}
                        horizental={false}
                        data={this.state.userList}
                        renderItem={({item})=>(
                            <TouchableOpacity 
                                onPress={()=>this.props.navigation.navigate('friendPage',{res: item,
                                                                                        friends: this.state.userFriends,
                                                                                        originalUser: this.state.username})}>
                                <Text>{item.data().name}</Text>
                            </TouchableOpacity>
                        )} />
                </View>
                <Button title="Log Out" onPress={()=> {this.props.navigation.navigate('Logout')}} />

            </View>
            )
    }
}

export default Home
