import React, { Component } from 'react'
import {View, Button, TextInput,FlatList,Text, TouchableOpacity, Image} from 'react-native'

import firebase from 'firebase'
require("firebase/firestore")

export class Home extends Component {

    constructor(props){
        super(props);
        this.state={
            username:'',
            userList: [],
            userFriends: [],
            allUsers:[],
            friendsInfo:[],
            friendsPosts:[],
            loading: false
        }
        this.searchUser = this.searchUser.bind(this)
    }

    componentDidMount(){
        console.log(firebase.auth().currentUser.data())
        let myFriends = []
        let timelinePosts = []        
        let users = []
        let myFriendsInfo = []
        // get username
        firebase.firestore()
            .collection('users')
            .doc(firebase.auth().currentUser.uid)
            .get()
            .then((infos)=>{
                this.setState({username:infos.data().name})
                // get user friends
                firebase.firestore()
                .collection('followers')
                .where('abonne','==',this.state.username)
                .get()
                .then((infos)=>{
                    infos.docs.map(doc => {
                        myFriends.push(doc.data().abonnement)                        
                    })
                    this.setState({userFriends:myFriends})
                    // get the userList
                    this.setState({userFriends:myFriends})
                    firebase.firestore()
                    .collection('users')
                    .get()
                    .then((infos)=>{
                        infos.docs.map(doc => {
                            users.push(doc)
                        })
                        this.setState({allUsers: users})
                        // get the full information of my friends
                        for (var i =0;i < this.state.userFriends.length; i++){
                            for (var j=0;j< this.state.allUsers.length;j++){
                                if (this.state.userFriends[i] == this.state.allUsers[j].data().name){
                                    myFriendsInfo.push(this.state.allUsers[j])
                                    j=this.state.allUsers.length
                                }
                            }
                            this.setState({friendsInfo:myFriendsInfo})
                        } 
                        // get the 2 latest posts of my friends
                        for (var ii=0; ii<this.state.friendsInfo.length;ii++){
                            firebase.firestore()
                            .collection('posts')
                            .doc(this.state.friendsInfo[ii].id)
                            .collection("userPosts")
                            .orderBy("date","asc")
                            .limit(2)
                            .get()
                            .then((infos)=>{
                                let posts = infos.docs.map(doc => {
                                    const data= doc.data();
                                    timelinePosts.push(data)
                                })
                                this.setState({friendsPosts: timelinePosts})
                                this.setState({loading:true})
                            })
                        }
                        /*timelinePosts.sort(function(a,b){
                            return new Date(b.date) - new Date(a.date)
                        })*/
                    })
                })
            })

    }

    searchUser(search){
        let myUsers = []
        firebase.firestore()
            .collection('users')
            .where('name','>=',search)
            .get()
            .then((infos)=>{
                infos.docs.map(doc => {
                    myUsers.push(doc)
                })
                this.setState({userList: myUsers})
            })
    }


    render() {
        if (this.state.loading){
        return (
            <View>
                <Text>Welcome Home ! </Text>                
                <Button title="Add Picture" onPress={()=> {this.props.navigation.navigate('addPicture')}}/>
                <Button title="Go to home page" onPress={()=> {this.props.navigation.navigate('homePage',{res:this.state.username})}}/>     
                <FlatList 
                        numColumns={1}
                        data={this.state.friendsPosts} 
                        renderItem = {({item}) => (
                        <View>
                            <Text>Description: {item.caption}</Text>
                            <Image 
                            style={{width:100,height:100}}
                            source={{uri: item.downloadURL}}/>
                        </View>)}
                    />
                <View>
                    <TextInput placeholder="Search User" onChangeText={(search)=>{this.searchUser(search)}}/>
                    <FlatList
                        numColumns={1}
                        horizental={false}
                        data={this.state.userList}
                        renderItem={({item})=>(
                            <TouchableOpacity 
                                onPress={()=>this.props.navigation.navigate('friendPage',{res: item,
                                                                                        friends: this.state.userFriends,
                                                                                        originalUser: this.state.username})}>
                                <Text>{item.data().name}</Text>
                            </TouchableOpacity>
                        )} />
                </View>
                <Button title="Log Out" onPress={()=> {this.props.navigation.navigate('Logout')}} />

            </View>
            )
        }
        return(
            <View>
                <Text>Loading...</Text>
            </View>
        )
    }
}

export default Home
